import React from "react"

class WeatherApp extends React.Component {
  constructor() {
    super()
    this.state = {
      city: "London", 
      cnt: "1",
      apiKey: "692d3bd12adf77c08728b7324d9f2b14",
      data: [],
    }
    this.handleChange = this.handleChange.bind(this)

  }

  componentDidMount() {
    const {city, cnt, apiKey} = this.state
    const apiURL = "api.openweathermap.org/data/2.5/forecast/daily?q=" + city + "&cnt=" + cnt + "&appid=%" + apiKey
    console.log(apiURL)
    fetch(apiURL)
      .then(response => response.json())
      .then(response => {
        console.log(response);
      })
      .catch(err => {
        console.log(err);
      });
        }

  handleChange(event) {
    const {name, value} = event.target
    this.setState({
      [name]: value
    })
  }

  handleSubmit() {

  }

  render() {
    const {city} = this.state
    return (
      <div className="weather-app">
         <form className="cityForm" onSubmit={this.handleSubmit}>
           <label>
             Enter your city:
            <input 
              type="text"
              name="city"
              value = {city}
              onChange={this.handleChange}/> 
           </label>
           <button>Submit</button>
         </form>
        <div className="displayWeather">
          <h1>City Name:<br /> {city}</h1>
        </div>

      </div>
    )
  }
}

export default WeatherApp
