import React from "react"

class WeatherApp extends React.Component {
  constructor() {
    super()
    this.state = {
      city: "", 
      data: []
    }
    this.handleChange = this.handleChange.bind(this)

  }

  componentDidMount() {
    fetch("https://community-open-weather-map.p.rapidapi.com/forecast/daily?q=san%20francisco%252Cus&lat=35&lon=139&cnt=10&units=metric%20or%20imperial", {
    "method": "GET",
    "headers": {
      "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
      "x-rapidapi-key": "569418ca3dmsh60d69364ab53974p13a133jsn2c004f135e06"
	  }
    })
      .then(response => {
        console.log(response);
      })
      .catch(err => {
        console.log(err);
      });
        }

  handleChange(event) {
    const {name, value} = event.target
    this.setState({
      [name]: value
    })
  }

  handleSubmit() {

  }

  render() {
    const {city} = this.state
    return (
      <div className="weather-app">
         <form className="cityForm" onSubmit={this.handleSubmit}>
           <label>
             Enter your city:
            <input 
              type="text"
              name="city"
              value = {city}
              onChange={this.handleChange}/> 
           </label>
           <button>Submit</button>
         </form>
        <div className="displayWeather">
          <h1>City Name:<br /> {city}</h1>
        </div>

      </div>
    )
  }
}

export default WeatherApp
