import React from "react"

function CurrentWeatherDisplay(props) {
  const weatherIcons = {
    rain: '<i class="fas fa-cloud-rain"></i>',
    clouds: '<i class="fas fa-cloud"></i>'
  }

  let currentWeatherIcon
  if (props.currentWeather === "Clouds") {
    currentWeatherIcon = weatherIcons.[props.currentWeather]
  }
  
  
  return (
    <div className="current-weather-display">
      <h1>{props.city}</h1>
      <h1>{props.lat}</h1>
      <h1>{props.long}</h1>
      <p>Current temperature: {props.currentTemp}Â°C</p>
      <p>Current weather: {props.currentWeather}</p>
      <p>Icon: <i class="fas fa-cloud"></i></p>
      <p>Sunrise: {props.currentSunrise}</p>
      <p>Sunset: {props.currentSunset}</p>

    </div>
  )
}

export default CurrentWeatherDisplay
