import React from "react"
import cities from "../data/cities"

class WeatherApp extends React.Component {
  constructor() {
    super()
    this.state = {
      lat: "52.24", //latitude
      long: "21.02", //longitude
      city: "warsaw", //city of choice
      part: "", //By using this parameter you can exclude some parts of the weather data from the API response. It should be a comma-delimited list (without spaces). Available values: "current", "minutely", "hourly", "daily"
      apiKey: "692d3bd12adf77c08728b7324d9f2b14", //APIkey for OpenWeatherMapAPI
      data: [], //data pulled from API
      
    }
    this.handleChange = this.handleChange.bind(this)

  }

  componentDidMount() {
    const {lat, long, part, apiKey} = this.state
    const apiURL = "https://api.openweathermap.org/data/2.5/onecall?lat=" + lat + "&lon=" + long + "&exclude=" + part + "&appid=" + apiKey
    console.log(apiURL)
    fetch(apiURL)
      .then(response => response.json())
      .then(res => {
        console.log(res);
      })
      .catch(err => {
        console.log(err);
      });
        }

  handleChange(event) {
    const {name, value} = event.target
    this.setState({
      [name]: value
    })
  }

  handleSubmit(event) {
    const {name, value} = event.target
  }

  render() {
    const {city, lat, long} = this.state
    return (
      <div className="weather-app">
        <div className="cityForm">
          <form onSubmit={this.handleSubmit}>
           <label>
             Enter your city:
            <input 
              type="text"
              name="city"
              value = {city}
              onChange={this.handleChange}/> 
           </label>
           <button>Submit</button>
         </form>
          <div className="displayWeather">
            <h1>City Name:<br /> {city}</h1>
          </div>
        </div>
         <br />
        <form className="cityForm" onSubmit={this.handleSubmit}>
           <label>
             Enter your latitude:
            <input 
              type="text"
              name="lat"
              value = {lat}
              onChange={this.handleChange}/> 
           </label>
           <button>Submit</button>
         </form>

      </div>
    )
  }
}

export default WeatherApp
