import React from "react"

function CurrentWeatherDisplay(props) {
  const {city, lat, long, currentTemp, currentWeather, currentDescription, currentIcon, currentSunrise, currentSunset} = props
  
  const weatherIcons = {
    clearSkyDay: '<i class="fas fa-sun"></i>',
    clearSkyNight: '<i class="fas fa-moon"></i>',
    fewCloudsDay: '<i class="fas fa-cloud-sun"></i>',
    fewCloudsNight: '<i class="fas fa-cloud-moon"></i>',
    clouds: '<i class="fas fa-cloud"></i>',
    rainDay: '<i class="fas fa-cloud-rain"></i>',
    rainNight: '<i class="fas fa-cloud-moon-rain"></i>',
    showeRain: '<i class="fas fa-cloud-showers-heavy"></i>',
    thunderstorm: '<i class="fas fa-bolt"></i>',
    snow: '<i class="fas fa-snowflake"></i>',
    mist: '<i class="fas fa-smog"></i>'
  }

  function getWeatherIcon(iconId) {
    if (iconId === "01d") {
      return (weatherIcon = '<i class="fas fa-sun"></i>')
    } else if (iconId === "01n") {
      return (weatherIcon = '<i class="fas fa-moon"></i>')
    } else if (iconId === "02d") {
      return (weatherIcon = '<i class="fas fa-cloud-sun"></i>')
    } else if (iconId === "02n") {
      return (weatherIcon = '<i class="fas fa-cloud-moon"></i>')
    } else if (iconId === "03d" || currentIcon === "03n") {
      return (weatherIcon = '<i class="fas fa-sun"></i>')
    } else if (iconId === "04d" || currentIcon === "04n") {
      return (weatherIcon = '<i class="fas fa-sun"></i>')
    } else if (iconId === "09d" || currentIcon === "09n") {
      return (weatherIcon = '<i class="fas fa-sun"></i>')
    } else if (iconId === "10d") {
      return (weatherIcon = '<i class="fas fa-sun"></i>')
    } else if (iconId === "10n") {
      return (weatherIcon = '<i class="fas fa-sun"></i>')
    } else if (iconId === "11d" || currentIcon === "11n") {
      return (weatherIcon = '<i class="fas fa-sun"></i>')
    } else if (iconId === "13d" || currentIcon === "13n") {
      return (weatherIcon = '<i class="fas fa-sun"></i>')
    } else if (iconId === "50d" || currentIcon === "50n") {
      return (weatherIcon = '<i class="fas fa-sun"></i>')
    } else {
      return (weatherIcon = '<i class="fas fa-sun"></i>')
    }
  }

  
  return (
    <div className="current-weather-display">
      <h1>{city}</h1>
      <h1>{lat}</h1>
      <h1>{long}</h1>
      <p>Current temperature: {currentTemp}Â°C</p>
      <p>Current weather: {currentWeather}</p>
      <p>Current description: {currentDescription}</p>
      <p>Icon: {currentIcon}</p>
      <p>Sunrise: {currentSunrise}</p>
      <p>Sunset: {currentSunset}</p>

    </div>
  )
}

export default CurrentWeatherDisplay
